def encodedIntToString(message):
    message = bin(message)
    message = message[2:] #remove 0b
    zeroToAdd = ((8 - len(message) % 8) % 8) * "0"
    message = zeroToAdd + message

    letter = ""
    word = ""
    for x in message:
        if len(letter) == 8:
            letter = int(letter, 2)
            letter = chr(letter)
            word += letter
            letter = ""
        letter += x
    letter = int(letter, 2)
    letter = chr(letter)
    word += letter
    return(word)


